let dbPromise=idb.open("posts-store",1,e=>{e.objectStoreNames.contains("posts")||e.createObjectStore("posts",{keyPath:"id"}),e.objectStoreNames.contains("sync-posts")||e.createObjectStore("sync-posts",{keyPath:"id"})});function writeData(e,t){return dbPromise.then(r=>{let o=r.transaction(e,"readwrite");return o.objectStore(e).put(t),o.complete})}function readAllData(e){return dbPromise.then(t=>{return t.transaction(e,"readonly").objectStore(e).getAll()})}function clearAllData(e){return dbPromise.then(t=>{let r=t.transaction(e,"readwrite");return r.objectStore(e).clear(),r.complete})}function deleteItemFromData(e,t){dbPromise.then(r=>{let o=r.transaction(e,"readwrite");return o.objectStore(e).delete(t),o.complete}).then(()=>{console.log("Item deleted from IndexedDB! id:",t)})}